mapper.select:
		select
        	
		from
				T_advertisement a
		
			
				AND a.createTime >= #{pd.beginDate}
			
			
				AND a.createTime <= #{pd.endDate}
			
			
				AND a.status = #{pd.status}
			
			
				AND (
					a.location LIKE CONCAT('%', #{pd.keyword},'%')
				or	a.title LIKE CONCAT('%', #{pd.keyword},'%')
				)
